import React from 'react';
import { useFormStatus } from 'react-dom';

async function mockSubmit(formData) {
  // Simulate a form submission with a delay
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(); // Simulate successful form submission
    }, 1000); // 1 second delay
  });
}

function TestFormStatus() {
  const { pending, submit } = useFormStatus();

  //form submission handler
  const handleSubmit = async (event) => {
    event.preventDefault(); // Prevent the default form submission behavior
    const formData = new FormData(event.target); // Get form data from the form element

    await submit(() => mockSubmit(formData)); // Call Submit function
    alert('Form submitted successfully!');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" name="name" placeholder="Enter your name" />
      <button type="submit" disabled={pending}>
        {pending ? 'Submitting...' : 'Submit'}
      </button>
    </form>
  );
}

export default TestFormStatus;
