import React from 'react';
import UpdateName from './components/UpdateName';
import SubmitForm from './components/SubmitForm';
import TestFormStatus from './components/TestFormStatus';
import { Suspense } from 'react';
import Greeting from './components/Greeting';
import ThemeContext from './components/ThemeContext';
import SimpleHeading from './components/SimpleHeading';
import ServerComponent from './components/ServerComponent';
import ClientComponent from './components/ClientComponent';
import ServerAction from './components/ServerAction';
import RefComponent from './components/RefComponent';
import SimpleContext from './components/SimpleContext';
import DeferredSearch from './components/DeferredSearch';
import PageWithMetadata from './components/PageWithMetadata';
import AsyncFunc from './components/AsyncFunc';
//import OptimisticNameUpdater from './components/OptimisticNameUpdater';
//import SimpleNameUpdater from './components/SimpleNameUpdater';
const theme = { color: 'green' };
function App() {
  return (
    <div className="App">
      <UpdateName />
      <SubmitForm/>
      <TestFormStatus />
      {/* <OptimisticNameUpdater/> */}
      <Suspense fallback={<div>Loading...</div>}> {/* Show this while waiting for the promise */}
      <Greeting /> {/* Render the Greeting component */}
    </Suspense>
    <ThemeContext.Provider value={theme}>
      <SimpleHeading />
    </ThemeContext.Provider>
    <ServerComponent/>
    <ClientComponent/>
    <ServerAction/>
    <RefComponent/>
    <SimpleContext/>
    <DeferredSearch/>
    <PageWithMetadata/>
    <AsyncFunc/>
    </div>
  );
}

export default App;
